import React, { useState } from 'react';
import Modal from 'react-modal';

// Define custom styles for the modal
const customStyles = {
    content: {
        width: '30%', // make the modal 3 times smaller
        margin: 'auto',
    },
};

Modal.setAppElement('#root'); // This line is needed for accessibility reasons

function BoxDisplay({ imgLink, title, cost }) {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    function openModal() {
        setModalIsOpen(true);
    }

    function closeModal() {
        setModalIsOpen(false);
    }

    return (
        <div className="box-display">
            <img src={imgLink} alt={title} />
            <h2>{title}</h2>
            <p>{cost}</p>
            <button onClick={openModal}>Reserve</button>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={closeModal}
                contentLabel="Reservation Modal"
                style={customStyles}
            >
                <h2>Reserve {title} Room</h2>
                <label>
                    Start Date:
                    <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
                </label>
                <label>
                    End Date:
                    <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
                </label>
                <button onClick={closeModal}>Close</button>
            </Modal>
            <button onClick={openModal} style={{ marginTop: '10px' }}>Reserve Room</button>
        </div>
    );
}

export default BoxDisplay;
