function Button({text="Button", color="black", fontSize=12}) {
    const [modalIsOpen, setModalIsOpen] = useState(false);

    function openModal() {
        setModalIsOpen(true);
    }

    function closeModal() {
        setModalIsOpen(false);
    }
    const buttonStyle = {
        color: color,
        fontSize: fontSize + "px",
        borderRadius: "10px",
    };

    return (
        <>
            <button style={buttonStyle} onClick={openModal}> {text} </button>
            {text === 'Reserve' && <Modal
            isOpen={modalIsOpen}
            onRequestClose={closeModal}
            contentLabel="Reservation Modal"
            style={customStyles}
            >
            <h2>Reserve {title} Room</h2>
            <label>
                Start Date:
                <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
            </label>
            <label>
                End Date:
                <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
            </label>
            {isAvailable ? <button onClick={addToCart}>Add to Cart</button> : <p>Room is not available on these dates.</p>}
            <button onClick={closeModal}>Close</button>
        </Modal> }
    </>
    )
}

export default Button;
