import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';

// Define custom styles for the modal
const customStyles = {
    content: {
        width: '30%', // make the modal 3 times smaller
        height: '30%',
        margin: 'auto',
    },
};

Modal.setAppElement('#root'); // This line is needed for accessibility reasons

function ProductBoxDisplay({ imgLink, title, cost, availability, btnAct="y"}) {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [isAvailable, setIsAvailable] = useState(true);

    function addToCart() {
        // Add your logic to add the product to the cart
    }

    function checkAvailability() {
        // Check if availability is an array before calling includes on it
        if (Array.isArray(availability)) {
            setIsAvailable(true);
        } else {
            setIsAvailable(false);
        }
    }

    // Call checkAvailability whenever startDate or endDate changes
    useEffect(() => {
        checkAvailability();
    }, [startDate, endDate]);

    return (
        <div className="box-display">
            <img src={imgLink} alt={title} />
            <h2>{title}</h2>
            <p>{cost}</p>
            {isAvailable ? <button onClick={addToCart}>Add to Cart</button> : <p>Product is out of stock.</p>}
        </div>
    );
}

export default ProductBoxDisplay;
